contracts/BountyMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./interfaces/IGeneticNFT.sol";

/**
 * @title BountyMarket - HelixVault Research Bounty Marketplace
 * @notice Allows researchers to post bounties for genomic data queries
 * @dev Researchers pay for answers to queries without accessing raw data
 */
contract BountyMarket is Ownable, ReentrancyGuard, Pausable {

    // ============ Structs ============

    struct Bounty {
        uint256 id;
        address researcher;       // Who created the bounty
        string queryType;         // Type: "SNP_CHECK", "TRAIT_QUERY", "VARIANT_SEARCH"
        string queryParams;       // JSON-encoded query parameters (e.g., {"rsid": "rs12913832"})
        uint256 rewardPerResponse; // Reward per valid response in wei
        uint256 maxResponses;     // Maximum number of responses wanted
        uint256 responseCount;    // Current number of responses
        uint256 totalFunded;      // Total ETH funded for this bounty
        uint256 remainingFunds;   // Remaining ETH for rewards
        uint256 createdAt;        // Creation timestamp
        uint256 expiresAt;        // Expiration timestamp
        bool isActive;            // Whether bounty is still accepting responses
    }

    struct Response {
        uint256 bountyId;
        uint256 tokenId;          // The Helix NFT token used
        address responder;        // NFT owner who responded
        bytes32 responseHash;     // Hash of the response (for verification)
        bytes zkProof;            // Zero-knowledge proof of the query result
        bool result;              // The boolean result (yes/no)
        uint256 timestamp;
        bool isPaid;              // Whether reward has been paid
    }

    // ============ State Variables ============

    IGeneticNFT public geneticNFT;

    uint256 private _bountyIdCounter;
    uint256 private _responseIdCounter;

    // Bounty ID => Bounty
    mapping(uint256 => Bounty) public bounties;

    // Response ID => Response
    mapping(uint256 => Response) public responses;

    // Bounty ID => Response IDs
    mapping(uint256 => uint256[]) public bountyResponses;

    // Token ID => Bounty ID => has responded
    mapping(uint256 => mapping(uint256 => bool)) public hasResponded;

    // Active bounty IDs
    uint256[] public activeBountyIds;

    // Platform fee percentage (in basis points, e.g., 250 = 2.5%)
    uint256 public platformFeeBps = 250;

    // Minimum bounty duration (1 hour)
    uint256 public constant MIN_DURATION = 1 hours;

    // Maximum bounty duration (30 days)
    uint256 public constant MAX_DURATION = 30 days;

    // ============ Events ============

    event BountyCreated(
        uint256 indexed bountyId,
        address indexed researcher,
        string queryType,
        uint256 rewardPerResponse,
        uint256 maxResponses,
        uint256 expiresAt
    );

    event BountyResponse(
        uint256 indexed bountyId,
        uint256 indexed responseId,
        uint256 indexed tokenId,
        address responder,
        bool result
    );

    event RewardPaid(
        uint256 indexed bountyId,
        uint256 indexed responseId,
        address indexed responder,
        uint256 amount
    );

    event BountyCancelled(uint256 indexed bountyId, uint256 refundAmount);

    event BountyExpired(uint256 indexed bountyId);

    // ============ Constructor ============

    constructor(address _geneticNFT) {
        geneticNFT = IGeneticNFT(_geneticNFT);
    }

    // ============ Researcher Functions ============

    /**
     * @notice Create a new research bounty
     * @param queryType The type of query (SNP_CHECK, TRAIT_QUERY, etc.)
     * @param queryParams JSON-encoded query parameters
     * @param rewardPerResponse Amount to pay per valid response
     * @param maxResponses Maximum number of responses wanted
     * @param duration How long the bounty should be active (in seconds)
     */
    function createBounty(
        string calldata queryType,
        string calldata queryParams,
        uint256 rewardPerResponse,
        uint256 maxResponses,
        uint256 duration
    ) external payable nonReentrant whenNotPaused returns (uint256) {
        require(bytes(queryType).length > 0, "Invalid query type");
        require(bytes(queryParams).length > 0, "Invalid query params");
        require(rewardPerResponse > 0, "Reward must be positive");
        require(maxResponses > 0, "Max responses must be positive");
        require(duration >= MIN_DURATION && duration <= MAX_DURATION, "Invalid duration");

        uint256 totalRequired = rewardPerResponse * maxResponses;
        uint256 platformFee = (totalRequired * platformFeeBps) / 10000;
        uint256 totalWithFee = totalRequired + platformFee;

        require(msg.value >= totalWithFee, "Insufficient funding");

        _bountyIdCounter++;
        uint256 bountyId = _bountyIdCounter;

        bounties[bountyId] = Bounty({
            id: bountyId,
            researcher: msg.sender,
            queryType: queryType,
            queryParams: queryParams,
            rewardPerResponse: rewardPerResponse,
            maxResponses: maxResponses,
            responseCount: 0,
            totalFunded: totalRequired,
            remainingFunds: totalRequired,
            createdAt: block.timestamp,
            expiresAt: block.timestamp + duration,
            isActive: true
        });

        activeBountyIds.push(bountyId);

        emit BountyCreated(
            bountyId,
            msg.sender,
            queryType,
            rewardPerResponse,
            maxResponses,
            block.timestamp + duration
        );

        // Refund excess payment (minus platform fee which stays)
        if (msg.value > totalWithFee) {
            payable(msg.sender).transfer(msg.value - totalWithFee);
        }

        return bountyId;
    }

    /**
     * @notice Cancel a bounty and get refund for remaining funds
     * @param bountyId The bounty to cancel
     */
    function cancelBounty(uint256 bountyId) external nonReentrant {
        Bounty storage bounty = bounties[bountyId];
        require(bounty.researcher == msg.sender, "Not bounty creator");
        require(bounty.isActive, "Bounty not active");

        bounty.isActive = false;
        uint256 refundAmount = bounty.remainingFunds;
        bounty.remainingFunds = 0;

        if (refundAmount > 0) {
            payable(msg.sender).transfer(refundAmount);
        }

        emit BountyCancelled(bountyId, refundAmount);
    }

    // ============ Responder Functions ============

    /**
     * @notice Respond to a bounty with a genomic query result
     * @param bountyId The bounty to respond to
     * @param tokenId The Helix NFT token ID being used
     * @param result The boolean result of the query
     * @param zkProof Zero-knowledge proof of the result
     */
    function respondToBounty(
        uint256 bountyId,
        uint256 tokenId,
        bool result,
        bytes calldata zkProof
    ) external nonReentrant whenNotPaused {
        Bounty storage bounty = bounties[bountyId];

        require(bounty.isActive, "Bounty not active");
        require(block.timestamp < bounty.expiresAt, "Bounty expired");
        require(bounty.responseCount < bounty.maxResponses, "Max responses reached");
        require(!hasResponded[tokenId][bountyId], "Already responded with this token");

        // Verify caller owns the token
        require(geneticNFT.ownerOf(tokenId) == msg.sender, "Not token owner");

        // Verify token data is active
        IGeneticNFT.GenomeData memory genomeData = geneticNFT.getGenomeData(tokenId);
        require(genomeData.isActive, "Genome data not active");

        hasResponded[tokenId][bountyId] = true;
        bounty.responseCount++;

        _responseIdCounter++;
        uint256 responseId = _responseIdCounter;

        bytes32 responseHash = keccak256(abi.encodePacked(
            bountyId,
            tokenId,
            msg.sender,
            result,
            block.timestamp
        ));

        responses[responseId] = Response({
            bountyId: bountyId,
            tokenId: tokenId,
            responder: msg.sender,
            responseHash: responseHash,
            zkProof: zkProof,
            result: result,
            timestamp: block.timestamp,
            isPaid: false
        });

        bountyResponses[bountyId].push(responseId);

        emit BountyResponse(bountyId, responseId, tokenId, msg.sender, result);

        // Auto-pay reward
        _payReward(bountyId, responseId);

        // Check if bounty is complete
        if (bounty.responseCount >= bounty.maxResponses) {
            bounty.isActive = false;
        }
    }

    // ============ View Functions ============

    /**
     * @notice Get all active bounties
     */
    function getActiveBounties() external view returns (Bounty[] memory) {
        uint256 activeCount = 0;

        // Count active bounties
        for (uint256 i = 0; i < activeBountyIds.length; i++) {
            if (bounties[activeBountyIds[i]].isActive && 
                block.timestamp < bounties[activeBountyIds[i]].expiresAt) {
                activeCount++;
            }
        }

        // Build result array
        Bounty[] memory result = new Bounty[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < activeBountyIds.length; i++) {
            Bounty memory b = bounties[activeBountyIds[i]];
            if (b.isActive && block.timestamp < b.expiresAt) {
                result[index] = b;
                index++;
            }
        }

        return result;
    }

    /**
     * @notice Get responses for a bounty
     * @param bountyId The bounty ID
     */
    function getBountyResponses(uint256 bountyId) external view returns (Response[] memory) {
        uint256[] memory responseIds = bountyResponses[bountyId];
        Response[] memory result = new Response[](responseIds.length);

        for (uint256 i = 0; i < responseIds.length; i++) {
            result[i] = responses[responseIds[i]];
        }

        return result;
    }

    /**
     * @notice Check if a token can respond to a bounty
     * @param tokenId The token ID
     * @param bountyId The bounty ID
     */
    function canRespond(uint256 tokenId, uint256 bountyId) external view returns (bool) {
        Bounty memory bounty = bounties[bountyId];

        if (!bounty.isActive) return false;
        if (block.timestamp >= bounty.expiresAt) return false;
        if (bounty.responseCount >= bounty.maxResponses) return false;
        if (hasResponded[tokenId][bountyId]) return false;

        try geneticNFT.getGenomeData(tokenId) returns (IGeneticNFT.GenomeData memory data) {
            return data.isActive;
        } catch {
            return false;
        }
    }

    // ============ Internal Functions ============

    function _payReward(uint256 bountyId, uint256 responseId) internal {
        Bounty storage bounty = bounties[bountyId];
        Response storage response = responses[responseId];

        require(!response.isPaid, "Already paid");
        require(bounty.remainingFunds >= bounty.rewardPerResponse, "Insufficient funds");

        response.isPaid = true;
        bounty.remainingFunds -= bounty.rewardPerResponse;

        payable(response.responder).transfer(bounty.rewardPerResponse);

        emit RewardPaid(bountyId, responseId, response.responder, bounty.rewardPerResponse);
    }

    // ============ Admin Functions ============

    /**
     * @notice Update platform fee
     * @param newFeeBps New fee in basis points
     */
    function setPlatformFee(uint256 newFeeBps) external onlyOwner {
        require(newFeeBps <= 1000, "Fee too high"); // Max 10%
        platformFeeBps = newFeeBps;
    }

    /**
     * @notice Update the GeneticNFT contract address
     * @param _geneticNFT New contract address
     */
    function setGeneticNFT(address _geneticNFT) external onlyOwner {
        geneticNFT = IGeneticNFT(_geneticNFT);
    }

    /**
     * @notice Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Withdraw platform fees
     */
    function withdrawFees() external onlyOwner {
        // Calculate fees (total balance minus all remaining bounty funds)
        uint256 totalBountyFunds = 0;
        for (uint256 i = 1; i <= _bountyIdCounter; i++) {
            totalBountyFunds += bounties[i].remainingFunds;
        }

        uint256 fees = address(this).balance - totalBountyFunds;
        if (fees > 0) {
            payable(owner()).transfer(fees);
        }
    }

    /**
     * @notice Process expired bounties
     * @param bountyIds Array of bounty IDs to check and expire
     */
    function processExpiredBounties(uint256[] calldata bountyIds) external {
        for (uint256 i = 0; i < bountyIds.length; i++) {
            Bounty storage bounty = bounties[bountyIds[i]];

            if (bounty.isActive && block.timestamp >= bounty.expiresAt) {
                bounty.isActive = false;

                // Refund remaining funds to researcher
                if (bounty.remainingFunds > 0) {
                    uint256 refund = bounty.remainingFunds;
                    bounty.remainingFunds = 0;
                    payable(bounty.researcher).transfer(refund);
                }

                emit BountyExpired(bountyIds[i]);
            }
        }
    }
}

contracts/GeneticNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title GeneticNFT - HelixVault Genomic Data NFT
 * @notice NFT representing ownership and access rights to encrypted genomic data
 * @dev The NFT acts as a KEY to decrypt off-chain genetic data stored on IPFS
 */
contract GeneticNFT is ERC721, ERC721URIStorage, ERC721Enumerable, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;

    // ============ Structs ============

    struct GenomeData {
        string ipfsCID;           // IPFS Content ID of encrypted file
        bytes32 dataHash;         // Keccak256 hash of original data for integrity verification
        string encryptionAlgo;    // Encryption algorithm used (e.g., "AES-256-GCM")
        string geneType;          // Type: "raw_sequence", "23andme", "ancestry", "vcf"
        uint256 mintTimestamp;    // When the NFT was minted
        uint256 fileSize;         // Size of the encrypted file in bytes
        bool isActive;            // Whether the genome data is still accessible
    }

    struct QueryAccess {
        address agent;            // Address of the AI agent with access
        uint256 expiry;           // Unix timestamp when access expires
        bool isValid;             // Whether access is currently valid
    }

    // ============ State Variables ============

    // Token ID => Genome Data
    mapping(uint256 => GenomeData) private _genomeData;

    // Token ID => Agent Address => Query Access
    mapping(uint256 => mapping(address => QueryAccess)) private _queryAccess;

    // Address => Token IDs owned (for easy lookup)
    mapping(address => uint256[]) private _ownedTokens;

    // Trusted AI agent addresses that can be granted access
    mapping(address => bool) public trustedAgents;

    // Platform fee for minting (in wei)
    uint256 public mintFee = 0.001 ether;

    // ============ Events ============

    event GenomeMinted(
        uint256 indexed tokenId,
        address indexed owner,
        string ipfsCID,
        string geneType,
        uint256 timestamp
    );

    event QueryAccessGranted(
        uint256 indexed tokenId,
        address indexed agent,
        uint256 expiry
    );

    event QueryAccessRevoked(
        uint256 indexed tokenId,
        address indexed agent
    );

    event GenomeDeactivated(uint256 indexed tokenId);

    event TrustedAgentUpdated(address indexed agent, bool trusted);

    // ============ Constructor ============

    constructor() ERC721("HelixVault Genome", "HELIX") {}

    // ============ Public Functions ============

    /**
     * @notice Mint a new Genome NFT
     * @param ipfsCID IPFS Content ID of the encrypted genetic data
     * @param dataHash Hash of the original unencrypted data for integrity
     * @param encryptionAlgo The encryption algorithm used
     * @param geneType Type of genetic data (23andme, ancestry, vcf, etc.)
     * @param fileSize Size of the encrypted file in bytes
     * @param tokenURI_ Metadata URI for the NFT
     */
    function mintGenome(
        string calldata ipfsCID,
        bytes32 dataHash,
        string calldata encryptionAlgo,
        string calldata geneType,
        uint256 fileSize,
        string calldata tokenURI_
    ) external payable nonReentrant returns (uint256) {
        require(msg.value >= mintFee, "Insufficient mint fee");
        require(bytes(ipfsCID).length > 0, "Invalid IPFS CID");
        require(dataHash != bytes32(0), "Invalid data hash");

        _tokenIds.increment();
        uint256 newTokenId = _tokenIds.current();

        _safeMint(msg.sender, newTokenId);
        _setTokenURI(newTokenId, tokenURI_);

        _genomeData[newTokenId] = GenomeData({
            ipfsCID: ipfsCID,
            dataHash: dataHash,
            encryptionAlgo: encryptionAlgo,
            geneType: geneType,
            mintTimestamp: block.timestamp,
            fileSize: fileSize,
            isActive: true
        });

        _ownedTokens[msg.sender].push(newTokenId);

        emit GenomeMinted(newTokenId, msg.sender, ipfsCID, geneType, block.timestamp);

        // Refund excess payment
        if (msg.value > mintFee) {
            payable(msg.sender).transfer(msg.value - mintFee);
        }

        return newTokenId;
    }

    /**
     * @notice Get genome data for a token (public metadata only)
     * @param tokenId The token ID to query
     */
    function getGenomeData(uint256 tokenId) external view returns (GenomeData memory) {
        require(_exists(tokenId), "Token does not exist");
        return _genomeData[tokenId];
    }

    /**
     * @notice Get the IPFS CID for a token (only owner or authorized agent)
     * @param tokenId The token ID to query
     */
    function getEncryptedDataCID(uint256 tokenId) external view returns (string memory) {
        require(_exists(tokenId), "Token does not exist");
        require(
            ownerOf(tokenId) == msg.sender || _hasValidAccess(tokenId, msg.sender),
            "Not authorized to access data"
        );
        require(_genomeData[tokenId].isActive, "Genome data is deactivated");

        return _genomeData[tokenId].ipfsCID;
    }

    /**
     * @notice Verify if an address has access to a token's data
     * @param tokenId The token ID to check
     * @param accessor The address to verify
     */
    function verifyAccess(uint256 tokenId, address accessor) external view returns (bool) {
        if (!_exists(tokenId)) return false;
        if (ownerOf(tokenId) == accessor) return true;
        return _hasValidAccess(tokenId, accessor);
    }

    /**
     * @notice Grant temporary query access to an AI agent
     * @param tokenId The token ID to grant access for
     * @param agent The AI agent address to grant access to
     * @param duration How long the access should last (in seconds)
     */
    function grantQueryAccess(
        uint256 tokenId,
        address agent,
        uint256 duration
    ) external {
        require(ownerOf(tokenId) == msg.sender, "Not token owner");
        require(trustedAgents[agent], "Agent is not trusted");
        require(duration > 0 && duration <= 1 days, "Invalid duration");

        uint256 expiry = block.timestamp + duration;

        _queryAccess[tokenId][agent] = QueryAccess({
            agent: agent,
            expiry: expiry,
            isValid: true
        });

        emit QueryAccessGranted(tokenId, agent, expiry);
    }

    /**
     * @notice Revoke query access from an AI agent
     * @param tokenId The token ID to revoke access for
     * @param agent The AI agent address to revoke access from
     */
    function revokeQueryAccess(uint256 tokenId, address agent) external {
        require(ownerOf(tokenId) == msg.sender, "Not token owner");

        _queryAccess[tokenId][agent].isValid = false;

        emit QueryAccessRevoked(tokenId, agent);
    }

    /**
     * @notice Deactivate genome data (owner can do this for privacy)
     * @param tokenId The token ID to deactivate
     */
    function deactivateGenome(uint256 tokenId) external {
        require(ownerOf(tokenId) == msg.sender, "Not token owner");

        _genomeData[tokenId].isActive = false;

        emit GenomeDeactivated(tokenId);
    }

    /**
     * @notice Get all token IDs owned by an address
     * @param owner The address to query
     */
    function getOwnedTokens(address owner) external view returns (uint256[] memory) {
        return _ownedTokens[owner];
    }

    /**
     * @notice Verify data integrity by checking hash
     * @param tokenId The token ID to verify
     * @param dataHash The hash to compare against
     */
    function verifyDataIntegrity(uint256 tokenId, bytes32 dataHash) external view returns (bool) {
        require(_exists(tokenId), "Token does not exist");
        return _genomeData[tokenId].dataHash == dataHash;
    }

    // ============ Admin Functions ============

    /**
     * @notice Add or remove a trusted AI agent
     * @param agent The agent address
     * @param trusted Whether the agent should be trusted
     */
    function setTrustedAgent(address agent, bool trusted) external onlyOwner {
        trustedAgents[agent] = trusted;
        emit TrustedAgentUpdated(agent, trusted);
    }

    /**
     * @notice Update the mint fee
     * @param newFee The new mint fee in wei
     */
    function setMintFee(uint256 newFee) external onlyOwner {
        mintFee = newFee;
    }

    /**
     * @notice Withdraw collected fees
     */
    function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    // ============ Internal Functions ============

    function _hasValidAccess(uint256 tokenId, address accessor) internal view returns (bool) {
        QueryAccess memory access = _queryAccess[tokenId][accessor];
        return access.isValid && access.expiry > block.timestamp;
    }

    // ============ Required Overrides ============

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
        delete _genomeData[tokenId];
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/interfaces/IGeneticNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title IGeneticNFT - Interface for GeneticNFT contract
 * @notice Interface used by BountyMarket and other contracts
 */
interface IGeneticNFT {

    struct GenomeData {
        string ipfsCID;
        bytes32 dataHash;
        string encryptionAlgo;
        string geneType;
        uint256 mintTimestamp;
        uint256 fileSize;
        bool isActive;
    }

    struct QueryAccess {
        address agent;
        uint256 expiry;
        bool isValid;
    }

    function mintGenome(
        string calldata ipfsCID,
        bytes32 dataHash,
        string calldata encryptionAlgo,
        string calldata geneType,
        uint256 fileSize,
        string calldata tokenURI_
    ) external payable returns (uint256);

    function getGenomeData(uint256 tokenId) external view returns (GenomeData memory);

    function getEncryptedDataCID(uint256 tokenId) external view returns (string memory);

    function verifyAccess(uint256 tokenId, address accessor) external view returns (bool);

    function grantQueryAccess(uint256 tokenId, address agent, uint256 duration) external;

    function revokeQueryAccess(uint256 tokenId, address agent) external;

    function deactivateGenome(uint256 tokenId) external;

    function getOwnedTokens(address owner) external view returns (uint256[] memory);

    function verifyDataIntegrity(uint256 tokenId, bytes32 dataHash) external view returns (bool);

    function ownerOf(uint256 tokenId) external view returns (address);

    function trustedAgents(address agent) external view returns (bool);
}

contracts/ResearcherRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title ResearcherRegistry - Researcher Verification System
 * @notice Manages researcher verification and credentials
 * @dev Only verified researchers can create bounties
 */
contract ResearcherRegistry is AccessControl, Pausable {

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    // ============ Enums ============

    enum VerificationStatus {
        NotRegistered,
        Pending,
        EmailVerified,
        FullyVerified,
        Suspended,
        Revoked
    }

    // ============ Structs ============

    struct Researcher {
        address wallet;
        string name;
        string institution;
        string email;
        string orcidId;
        string researchField;
        string irbApprovalNumber;
        VerificationStatus status;
        uint256 registeredAt;
        uint256 verifiedAt;
        uint256 reputationScore;
        uint256 totalBounties;
        uint256 successfulBounties;
        bool isActive;
    }

    struct VerificationRequest {
        address researcher;
        string documentsHash;
        string notes;
        uint256 submittedAt;
        bool isProcessed;
    }

    // ============ State Variables ============

    mapping(address => Researcher) public researchers;
    mapping(string => bool) public usedEmails;

    mapping(uint256 => VerificationRequest) public verificationRequests;
    uint256 private _requestIdCounter;

    address[] public allResearchers;

    uint256 public minReputationScore = 50;

    // ============ Events ============

    event ResearcherRegistered(
        address indexed researcher,
        string name,
        string institution,
        string email
    );

    event VerificationRequested(
        uint256 indexed requestId,
        address indexed researcher,
        string documentsHash
    );

    event ResearcherVerified(
        address indexed researcher,
        VerificationStatus status,
        address indexed verifier
    );

    event ResearcherSuspended(
        address indexed researcher,
        string reason,
        address indexed admin
    );

    event ResearcherReactivated(
        address indexed researcher,
        address indexed admin
    );

    event ReputationUpdated(
        address indexed researcher,
        uint256 oldScore,
        uint256 newScore
    );

    event BountyStatsUpdated(
        address indexed researcher,
        uint256 totalBounties,
        uint256 successfulBounties
    );

    // ============ Constructor ============

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
    }

    // ============ Researcher Functions ============

    function registerResearcher(
        string calldata name,
        string calldata institution,
        string calldata email,
        string calldata researchField
    ) external whenNotPaused {
        require(researchers[msg.sender].wallet == address(0), "Already registered");
        require(bytes(name).length > 0, "Name required");
        require(bytes(institution).length > 0, "Institution required");
        require(bytes(email).length > 0, "Email required");
        require(!usedEmails[email], "Email already used");

        researchers[msg.sender] = Researcher({
            wallet: msg.sender,
            name: name,
            institution: institution,
            email: email,
            orcidId: "",
            researchField: researchField,
            irbApprovalNumber: "",
            status: VerificationStatus.Pending,
            registeredAt: block.timestamp,
            verifiedAt: 0,
            reputationScore: 50,
            totalBounties: 0,
            successfulBounties: 0,
            isActive: true
        });

        usedEmails[email] = true;
        allResearchers.push(msg.sender);

        emit ResearcherRegistered(msg.sender, name, institution, email);
    }

    function submitVerificationDocuments(
        string calldata documentsHash,
        string calldata notes
    ) external {
        require(researchers[msg.sender].wallet != address(0), "Not registered");
        require(
            researchers[msg.sender].status == VerificationStatus.Pending ||
            researchers[msg.sender].status == VerificationStatus.EmailVerified,
            "Invalid status"
        );

        _requestIdCounter++;
        uint256 requestId = _requestIdCounter;

        verificationRequests[requestId] = VerificationRequest({
            researcher: msg.sender,
            documentsHash: documentsHash,
            notes: notes,
            submittedAt: block.timestamp,
            isProcessed: false
        });

        emit VerificationRequested(requestId, msg.sender, documentsHash);
    }

    function updateProfile(
        string calldata orcidId,
        string calldata irbApprovalNumber
    ) external {
        require(researchers[msg.sender].wallet != address(0), "Not registered");

        researchers[msg.sender].orcidId = orcidId;
        researchers[msg.sender].irbApprovalNumber = irbApprovalNumber;
    }

    // ============ INTERNAL VERIFICATION LOGIC ============

    function _verifyResearcherInternal(
        address researcherAddress,
        VerificationStatus status
    ) internal {
        Researcher storage researcher = researchers[researcherAddress];
        researcher.status = status;

        if (status == VerificationStatus.FullyVerified && researcher.verifiedAt == 0) {
            researcher.verifiedAt = block.timestamp;
        }

        if (
            status == VerificationStatus.Suspended ||
            status == VerificationStatus.Revoked
        ) {
            researcher.isActive = false;
        }

        emit ResearcherVerified(researcherAddress, status, msg.sender);
    }

    // ============ Admin / Verifier Functions ============

    function verifyResearcher(
        address researcherAddress,
        VerificationStatus status
    ) external onlyRole(VERIFIER_ROLE) {
        require(researchers[researcherAddress].wallet != address(0), "Not registered");

        require(
            status == VerificationStatus.EmailVerified ||
            status == VerificationStatus.FullyVerified ||
            status == VerificationStatus.Suspended ||
            status == VerificationStatus.Revoked,
            "Invalid status"
        );

        _verifyResearcherInternal(researcherAddress, status);
    }

    function processVerificationRequest(
        uint256 requestId,
        bool approved
    ) external onlyRole(VERIFIER_ROLE) {
        VerificationRequest storage request = verificationRequests[requestId];
        require(!request.isProcessed, "Already processed");

        request.isProcessed = true;

        if (approved) {
            _verifyResearcherInternal(
                request.researcher,
                VerificationStatus.FullyVerified
            );
        }
    }

    function suspendResearcher(
        address researcherAddress,
        string calldata reason
    ) external onlyRole(ADMIN_ROLE) {
        require(researchers[researcherAddress].wallet != address(0), "Not registered");

        researchers[researcherAddress].status = VerificationStatus.Suspended;
        researchers[researcherAddress].isActive = false;

        emit ResearcherSuspended(researcherAddress, reason, msg.sender);
    }

    function reactivateResearcher(
        address researcherAddress
    ) external onlyRole(ADMIN_ROLE) {
        require(researchers[researcherAddress].wallet != address(0), "Not registered");
        require(
            researchers[researcherAddress].status == VerificationStatus.Suspended,
            "Not suspended"
        );

        researchers[researcherAddress].status = VerificationStatus.FullyVerified;
        researchers[researcherAddress].isActive = true;

        emit ResearcherReactivated(researcherAddress, msg.sender);
    }

    function updateReputation(
        address researcherAddress,
        uint256 newScore
    ) external onlyRole(ADMIN_ROLE) {
        require(researchers[researcherAddress].wallet != address(0), "Not registered");
        require(newScore <= 100, "Score must be 0-100");

        uint256 oldScore = researchers[researcherAddress].reputationScore;
        researchers[researcherAddress].reputationScore = newScore;

        emit ReputationUpdated(researcherAddress, oldScore, newScore);
    }

    function updateBountyStats(
        address researcherAddress,
        bool isSuccessful
    ) external {
        // TODO: restrict to BountyMarket contract
        require(researchers[researcherAddress].wallet != address(0), "Not registered");

        Researcher storage researcher = researchers[researcherAddress];
        researcher.totalBounties++;

        if (isSuccessful) {
            researcher.successfulBounties++;
        }

        emit BountyStatsUpdated(
            researcherAddress,
            researcher.totalBounties,
            researcher.successfulBounties
        );
    }

    function setMinReputationScore(uint256 score) external onlyRole(ADMIN_ROLE) {
        require(score <= 100, "Score must be 0-100");
        minReputationScore = score;
    }

    // ============ View Functions ============

    function isVerifiedResearcher(
        address researcherAddress
    ) external view returns (bool) {
        Researcher memory researcher = researchers[researcherAddress];
        return
                researcher.status == VerificationStatus.FullyVerified &&
                researcher.isActive &&
                researcher.reputationScore >= minReputationScore;
    }

    function getResearcher(
        address researcherAddress
    ) external view returns (Researcher memory) {
        return researchers[researcherAddress];
    }

    function getAllResearchers() external view returns (address[] memory) {
        return allResearchers;
    }

    function getPendingRequests() external view returns (uint256[] memory) {
        uint256 pendingCount;

        for (uint256 i = 1; i <= _requestIdCounter; i++) {
            if (!verificationRequests[i].isProcessed) {
                pendingCount++;
            }
        }

        uint256[] memory result = new uint256[](pendingCount);
        uint256 index;

        for (uint256 i = 1; i <= _requestIdCounter; i++) {
            if (!verificationRequests[i].isProcessed) {
                result[index++] = i;
            }
        }

        return result;
    }

    // ============ Pausable ============

    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }
}

